equalheight = function(container){

	var currentTallest = 0,
		 currentRowStart = 0,
		 rowDivs = new Array(),
		 $el,
		 topPosition = 0;
	 $(container).each(function() {
	
	   $el = $(this);
	   $($el).height('auto')
	   topPostion = $el.position().top;
	
	   if (currentRowStart != topPostion) {
		 for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {
		   rowDivs[currentDiv].height(currentTallest);
		 }
		 rowDivs.length = 0; // empty the array
		 currentRowStart = topPostion;
		 currentTallest = $el.height();
		 rowDivs.push($el);
	   } else {
		 rowDivs.push($el);
		 currentTallest = (currentTallest < $el.height()) ? ($el.height()) : (currentTallest);
	  }
	   for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {
		 rowDivs[currentDiv].height(currentTallest);
	   }
	 });
}

function responheight() {
	var hfoot = $('.footer-btn').outerHeight(),
		hbotnav = $('.bot-list').height();
		/*htask = $('.main-content-wrap').delay(3000).height()-40;*/
	$('.main-content').css({paddingBottom:hfoot+25});	
	$('.nav-list .nav-wrap').css({paddingBottom:hbotnav});
	/*$('.event-wrap').height(htask);*/	
	
}


$(window).load(function() {
  //equalheight('.event-detail .box');
  //responheight();
  //scroll
	$('.scroll-pane').jScrollPane();
	
});

$(window).resize(function(){
  //equalheight('.event-detail .box');
  responheight();//scroll
	$('.scroll-pane').jScrollPane();

});

jQuery(function($) {	
	
	// placeholder
	placeHolderFallBack();	
	
	$('ul li:first-child').addClass('first');
	$('ul li:last-child').addClass('last');
	
	$('.ico').tooltip();
	
	$('.go-top').click(function(){$('html, body').animate({scrollTop:0},1000);});
	
	$('.select-type, .radio-type, .check-type').uniform();
	
	$('.event-link').each(function() {
    	$(this).click(function(){
			$(".show-event").hide();
			$(this).next().show();
		});
    });
	$('.btn-close').each(function() {
    	$(this).click(function(){
        	$(this).parent().hide();
		});
    });
	
});

function runDatePicker(url, format) {
    $('.my-datepicker-control').each(function(){
        var id = $(this).attr('id');
        $('#'+id).datepicker(
            jQuery.extend(
                {showMonthAfterYear:false}, 
                jQuery.datepicker.regional['en-GB'], 
                {
                    'dateFormat':format,
                    'regional':'en_us',
                    'changeMonth':'true',
                    'changeYear':'true',
                    
                    'showOn':'button',
                    'buttonImage': url + '/img/icon_calendar_r.gif',
                    'buttonImageOnly':true
                }
            )
        );
    });
}

function runDobPicker(url, format) {
    $('.my-dob-datepicker').each(function(){
        var id = $(this).attr('id');
        $('#'+id).datepicker(
            jQuery.extend(
                {showMonthAfterYear:false}, 
                jQuery.datepicker.regional['en-GB'], 
                {
                    'dateFormat':'dd/mm/yy',
                    'regional':'en_us',
                    'changeMonth':'true',
                    'changeYear':'true',
                    'yearRange': '1920:+0',
                }
            )
        );
    });
}

